/**
 Given a 2D board and a word, find if the word exists in the grid.

 The word can be constructed from letters of sequentially adjacent cell, where "adjacent" cells are those horizontally or vertically neighboring. The same letter cell may not be used more than once.

 Example:

 board =
 [
   ["A","B","C","E"],
   ["S","F","C","S"],
   ["A","D","E","E"]
 ]

 Given word = "ABCCED", return true.
 Given word = "SEE", return true.
 Given word = "ABCB", return false.
  

 Constraints:

 board and word consists only of lowercase and uppercase English letters.
 1 <= board.length <= 200
 1 <= board[i].length <= 200
 1 <= word.length <= 10^3
 */

class Solution {
    var board: [[Character]]!
    var word: [String.Element]!
    var found: Bool = false
    
    func exist(_ board: [[Character]], _ word: String) -> Bool {
        guard !board.isEmpty, !board[0].isEmpty else { return false }
        
        self.board = board
        self.word = Array(word)
        
        for i in 0..<board.count {
            for j in 0..<board[0].count {
                if dfs(i: i, j: j, w: 0) {
                    return true
                }
            }
        }
        
        return false
    }
    
    /// i - row
    /// j - column
    /// w - letter index in word
    private func dfs(i: Int, j: Int, w: Int) -> Bool {
        if w == word.count {
            return true
        }
        if i < 0 || j < 0 || i >= board.count || j >= board[0].count {
            return false
        }
        if board[i][j] == "0" {
            return false
        }
        var ret = false
        if board[i][j] == word[w] {
            let letter = board[i][j]
            board[i][j] = "0"
            let rows = [-1, 1, 0, 0]
            let cols = [ 0, 0, -1, 1]
            for k in 0..<4 {
                ret = dfs(i: i + rows[k], j: j + cols[k], w: w + 1)
                
                if ret {
                    break
                }
            }
            board[i][j] = letter
        }
        
        return ret
    }
}

